$(document).ready(function(){var e=$("#results-template").html(),t=Handlebars.compile(e),n=document.getElementById("results"),a=function(e){$.ajax({url:"https://api.spotify.com/v1/search",data:{q:e,type:"album"},success:function(e){n.innerHTML=t(e)}})};$("#search-form").on("submit",function(e){e.preventDefault(),a($("#query").val())})});